import { useState } from "react";

export default function Post(props) {
  let [iconSalvar, setSalvar] = (useState = "bookmark-outline");

  function salvarPost() {
    if (iconSalvar === "bookmark-outilne") {
      setSalvar("bookmark");
    } else {
      setSalvar("bookmark-outine");
    }
  }

  return (
    <div>
      <div class="post">
        <div class="topo">
          <div class="usuario">
            <img src={props.imagemUsuario} alt={props.altUsuario} />
            <p>{props.nomeUsuario}</p>
          </div>
          <div class="acoes">
            <ion-icon name="ellipsis-horizontal"></ion-icon>
          </div>
        </div>

        <div class="conteudo">
          <img src={props.imagemPost} alt={props.altPost} />
        </div>

        <div class="fundo">
          <div class="acoes">
            <div>
              <ion-icon name="heart-outline"></ion-icon>
              <ion-icon name="chatbubble-outline"></ion-icon>
              <ion-icon name="paper-plane-outline"></ion-icon>
            </div>
            <div>
              <ion-icon onClick={salvarPost} name={iconSalvar}></ion-icon>
            </div>
          </div>

          <div class="curtidas">
            <img src={props.imagemCurtida} alt={props.altCurtida} />
            <div class="texto">
              Curtido por <strong>{props.usuarioCurtida}</strong> e{" "}
              <strong>outras {props.numCurtidas} pessoas</strong>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
