import { useState } from "react";

export default function User() {
  let [usuario, setUsuario] = useState("catanacomics");
  let [imagem, setImagem] = useState("assets/img/catanacomics.svg");

  function mudarUsuario() {
    let novoUsuario = prompt("Digite seu nome de usuário.");
    if (novoUsuario !== "") setUsuario(novoUsuario);
  }

  function mudarImagem() {
    let novaImagem = prompt("Insira o link de uma imagem.");
    if (novaImagem !== "") setImagem(novaImagem);
  }

  return (
    <div class="usuario">
      <img onClick={mudarImagem} src={imagem} alt="imagem de perfil" />
      <div class="texto">
        <span>
          <strong> {usuario} </strong>
          <ion-icon onClick={mudarUsuario} name="pencil"></ion-icon>
        </span>
      </div>
    </div>
  );
}
