import { useState } from "react";

export default function Post(props) {
  let [iconeSalvar, setSalvar] = useState("bookmark-outline");

  function salvarPost(icone) {
    if (icone === "bookmark-outline") setSalvar("bookmark");
    else setSalvar("bookmark-outline");
  }

  function curtirPost() {}

  return (
    <div>
      <div className="post">
        <div className="topo">
          <div className="usuario">
            <img src={props.imagemUsuario} alt={props.altUsuario} />
            <p>{props.nomeUsuario}</p>
          </div>
          <div className="acoes">
            <ion-icon name="ellipsis-horizontal"></ion-icon>
          </div>
        </div>

        <div className="conteudo">
          <img src={props.imagemPost} alt={props.altPost} />
        </div>

        <div className="fundo">
          <div className="acoes">
            <div>
              <ion-icon class="curtido" name="heart-outline"></ion-icon>
              <ion-icon name="chatbubble-outline"></ion-icon>
              <ion-icon name="paper-plane-outline"></ion-icon>
            </div>
            <div>
              <ion-icon
                onClick={() => salvarPost(iconeSalvar)}
                name={iconeSalvar}
              ></ion-icon>
            </div>
          </div>

          <div className="curtidas">
            <img src={props.imagemCurtida} alt={props.altCurtida} />
            <div className="texto">
              Curtido por <strong>{props.usuarioCurtida}</strong> e{" "}
              <strong>outras {props.numCurtidas} pessoas</strong>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
